// src/SubmitCar.js
import React, { useState } from 'react';
import './style.css'; // Import shared styles

const SubmitCar = () => {
  // Define local state for form fields
  const [carData, setCarData] = useState({
    make: '',
    model: '',
    year: '',
    price: '',
  });

  // Handle input changes
  const handleChange = (e) => {
    setCarData({
      ...carData,
      [e.target.name]: e.target.value,
    });
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();

    // Create a new car object with 'pending' status
    const newCar = {
      id: Date.now(), // Unique identifier
      ...carData,
      status: 'pending',
    };

    // Retrieve existing submissions from localStorage
    const existing = JSON.parse(localStorage.getItem('carSubmissions')) || [];

    // Append new submission
    const updated = [...existing, newCar];

    // Save back to localStorage
    localStorage.setItem('carSubmissions', JSON.stringify(updated));

    // Optionally show confirmation (does not reset form)
    alert('Car submitted for review!');
  };

  return (
    <div className="container">
      <h2>Submit a Used Car</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="make">Car Make</label>
          <input
            className="form-input"
            type="text"
            id="make"
            name="make"
            value={carData.make}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="model">Model</label>
          <input
            className="form-input"
            type="text"
            id="model"
            name="model"
            value={carData.model}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="year">Year</label>
          <input
            className="form-input"
            type="text"
            id="year"
            name="year"
            value={carData.year}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="price">Price</label>
          <input
            className="form-input"
            type="text"
            id="price"
            name="price"
            value={carData.price}
            onChange={handleChange}
            required
          />
        </div>

        <button className="primary-button" type="submit">
          Submit for Review
        </button>
      </form>
    </div>
  );
};

export default SubmitCar;
