// src/ManageUsers.js
// Programming Lab 2: User registration, role-based access, and state handling with localStorage

import React, { useState, useEffect } from 'react';
import './style.css';

const ManageUsers = () => {
  const [users, setUsers] = useState([]);
  const [newName, setNewName] = useState('');
  const [currentUser, setCurrentUser] = useState('');

  // Lab 2: Load users and current user from localStorage on mount
  useEffect(() => {
    const storedUsers = JSON.parse(localStorage.getItem('registeredUsers')) || [];
    const savedCurrentUser = localStorage.getItem('currentUser') || '';

    // Ensure 'admin' user exists
    const adminExists = storedUsers.some(user => user.name === 'admin');
    let updatedUsers = [...storedUsers];

    if (!adminExists) {
      const adminUser = {
        id: 'admin',
        name: 'admin',
        role: 'Admin',
      };
      updatedUsers.unshift(adminUser);
    }

    setUsers(updatedUsers);
    setCurrentUser(savedCurrentUser);
    localStorage.setItem('registeredUsers', JSON.stringify(updatedUsers));
  }, []);

  // Lab 2: Add new user to the list and auto-login
  const handleAddUser = () => {
    if (newName.trim() === '') return;

    const duplicate = users.some(user => user.name.toLowerCase() === newName.trim().toLowerCase());
    if (duplicate) {
      alert('User already exists.');
      return;
    }

    const newUser = {
      id: Date.now().toString() + '-' + Math.random().toString(36).substring(2, 8),
      name: newName.trim(),
      role: 'User',
    };

    const updatedUsers = [...users, newUser];
    setUsers(updatedUsers);
    localStorage.setItem('registeredUsers', JSON.stringify(updatedUsers));

    // Lab 2: Automatically log in new user
    setCurrentUser(newUser.name);
    localStorage.setItem('currentUser', newUser.name);

    setNewName('');
  };

  // Lab 2: Delete user (except admin)
  const handleDeleteUser = (id) => {
    const userToDelete = users.find(user => user.id === id);
    if (userToDelete && userToDelete.role === 'Admin') {
      alert('Cannot delete the admin user.');
      return;
    }

    const updatedUsers = users.filter(user => user.id !== id);
    setUsers(updatedUsers);
    localStorage.setItem('registeredUsers', JSON.stringify(updatedUsers));

    // Optional: log out deleted user
    if (userToDelete.name === currentUser) {
      setCurrentUser('');
      localStorage.removeItem('currentUser');
    }
  };

  // Lab 2: Login as selected user
  const handleLoginAs = (name) => {
    setCurrentUser(name);
    localStorage.setItem('currentUser', name);
  };

  return (
    <div className="container">
      <h1 className="dashboard-title">Manage Users</h1>

      {/* Display currently logged in user */}
      <p style={{ fontWeight: 'bold', color: '#333' }}>
        Currently logged in as: <span style={{ color: '#007bff' }}>{currentUser || 'None'}</span>
      </p>

      {/* User input form */}
      <div className="user-form">
        <input
          type="text"
          placeholder="Enter name"
          value={newName}
          onChange={(e) => setNewName(e.target.value)}
          className="form-input"
        />
        <button onClick={handleAddUser} className="primary-button add-button">
          Add
        </button>
      </div>

      {/* User list */}
      {users.length === 0 ? (
        <p style={{ color: '#888', textAlign: 'center' }}>
          No users found. Please add one above.
        </p>
      ) : (
        <ul className="user-list">
          {users.map(user => (
            <li key={user.id} className="user-row">
              <span className="user-name">{user.name}</span>
              <span className="user-role-label">{user.role}</span>

              {/* Only show delete for non-admin */}
              {user.role !== 'Admin' && (
                <button
                  onClick={() => handleDeleteUser(user.id)}
                  className="delete-button"
                >
                  Delete
                </button>
              )}

              {/* Login button for all users */}
              <button
                onClick={() => handleLoginAs(user.name)}
                className="login-button"
              >
                Login
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default ManageUsers;
