// src/ManageUsers.js
import React, { useState, useEffect } from 'react';
import './style.css';

const ManageUsers = () => {
  // Load users from localStorage on component mount
  const [users, setUsers] = useState([]);

  useEffect(() => {
    const storedUsers = JSON.parse(localStorage.getItem('registeredUsers')) || [];
    setUsers(storedUsers);
  }, []);

  const [newName, setNewName] = useState('');
  const [newRole, setNewRole] = useState('User');

  const handleAddUser = () => {
    if (newName.trim() === '') return;

    const newUser = {
      id: Date.now(),
      name: newName.trim(),
      role: newRole,
    };

    const updatedUsers = [...users, newUser];
    setUsers(updatedUsers);
    localStorage.setItem('registeredUsers', JSON.stringify(updatedUsers)); // save to localStorage

    setNewName('');
    setNewRole('User');
  };

  const handleRoleChange = (id, updatedRole) => {
    const updatedUsers = users.map(user =>
      user.id === id ? { ...user, role: updatedRole } : user
    );
    setUsers(updatedUsers);
    localStorage.setItem('registeredUsers', JSON.stringify(updatedUsers)); // sync
  };

  const handleDeleteUser = (id) => {
    const updatedUsers = users.filter(user => user.id !== id);
    setUsers(updatedUsers);
    localStorage.setItem('registeredUsers', JSON.stringify(updatedUsers)); // sync
  };

  return (
    <div className="container">
      <h1 className="dashboard-title">Manage Users</h1>
      <p className="dashboard-welcome">List of registered users:</p>

      {/* Add user form */}
      <div className="user-form">
        <input
          type="text"
          placeholder="Enter name"
          value={newName}
          onChange={(e) => setNewName(e.target.value)}
          className="form-input"
        />
        <select
          value={newRole}
          onChange={(e) => setNewRole(e.target.value)}
          className="form-select"
        >
          <option value="User">User</option>
          <option value="Admin">Admin</option>
        </select>
        <button onClick={handleAddUser} className="primary-button add-button">
          Add
        </button>
      </div>

      {/* User list */}
      <ul className="user-list">
        {users.map(user => (
          <li key={user.id} className="user-row">
            <span className="user-name">{user.name}</span>
            <select
              value={user.role}
              onChange={(e) => handleRoleChange(user.id, e.target.value)}
              className="form-select user-role"
            >
              <option value="User">User</option>
              <option value="Admin">Admin</option>
            </select>
            <button
              onClick={() => handleDeleteUser(user.id)}
              className="delete-button"
            >
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ManageUsers;
