// src/Listings.js
import React, { useEffect, useState } from 'react';

const Listings = () => {
  const [approvedCars, setApprovedCars] = useState([]);

  // Load approved cars from localStorage on mount
  useEffect(() => {
    loadApprovedCars();
  }, []);

  // Load approved cars
  const loadApprovedCars = () => {
    const stored = JSON.parse(localStorage.getItem('submittedCars')) || [];
    const approved = stored.filter(car => car.approved === true);
    setApprovedCars(approved);
  };

  // Delete a car by index
  const handleDelete = (indexToDelete) => {
    const stored = JSON.parse(localStorage.getItem('submittedCars')) || [];
    const carToDelete = approvedCars[indexToDelete];
    let removed = false;

    const updated = stored.filter(car => {
      if (!removed &&
          car.approved === true &&
          car.make === carToDelete.make &&
          car.model === carToDelete.model &&
          car.year === carToDelete.year &&
          car.price === carToDelete.price &&
          car.location === carToDelete.location) {
        removed = true;
        return false;
      }
      return true;
    });

    localStorage.setItem('submittedCars', JSON.stringify(updated));
    loadApprovedCars();
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold text-center mb-6">Browse Car Listings</h1>

      {approvedCars.length === 0 ? (
        <p className="text-center text-gray-600">No approved listings available.</p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {approvedCars.map((car, index) => (
            <div key={index} className="bg-white rounded-xl shadow-lg p-4">
              <h2 className="text-xl font-semibold mb-2">
                {car.make} {car.model}
              </h2>
              <p className="text-gray-700 mb-2">
                <strong>Year:</strong> {car.year}<br />
                <strong>Price:</strong> ${car.price}<br />
                <strong>Mileage:</strong> {car.mileage} km<br />
                <strong>Transmission:</strong> {car.transmission}<br />
                <strong>Fuel Type:</strong> {car.fuelType}<br />
                <strong>Location:</strong> {car.location}<br />
              </p>
              {car.imageUrl && (
                <img src={car.imageUrl} alt="Car" className="w-full h-48 object-cover rounded mb-2" />
              )}
              <p className="text-gray-600 italic mb-2">{car.description}</p>

              <button
                className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
                onClick={() => handleDelete(index)}
              >
                Delete
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Listings;
