import React, { useEffect, useState } from 'react';

function TodoList() {
  const [tasks, setTasks] = useState([]);

  // 从 NHTSA API 加载数据
  useEffect(() => {
    fetch('https://vpic.nhtsa.dot.gov/api/vehicles/getallmakes?format=json')
      .then((res) => res.json())
      .then((data) => {
        const carMakes = data.Results.slice(0, 10).map((item) => item.Make_Name);
        setTasks(carMakes);
      })
      .catch((err) => console.error('Failed to fetch car makes:', err));
  }, []);

  // 删除任务
  const handleDelete = (index) => {
    const newTasks = [...tasks];
    newTasks.splice(index, 1);
    setTasks(newTasks);
  };

  return (
    <div className="container">
      <h2>Top 10 Car Makes (from NHTSA API)</h2>
      <ul>
        {tasks.map((make, index) => (
          <li
            key={index}
            style={{
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              backgroundColor: '#f9f9f9',
              padding: '10px',
              marginBottom: '10px',
              borderRadius: '6px'
            }}
          >
            <span>{make}</span>
            <button
              style={{
                backgroundColor: '#007bff',
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                padding: '5px 10px',
                cursor: 'pointer'
              }}
              onClick={() => handleDelete(index)}
            >
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default TodoList;
