// src/pages/CarReview.js
import React, { useEffect, useState } from 'react';
import './style.css';

const CarReview = () => {
  const [cars, setCars] = useState([]);

  // Simulated fetch from backend API
  useEffect(() => {
    // Mock data, in real app fetch from API
    const mockData = [
      { id: 1, model: 'Toyota Corolla', year: 2020, status: 'pending' },
      { id: 2, model: 'Honda Civic', year: 2019, status: 'pending' }
    ];
    setCars(mockData);
  }, []);

  // Approve car function
  const handleApprove = (id) => {
    setCars(prev => prev.map(car => car.id === id ? { ...car, status: 'approved' } : car));
  };

  // Reject car function
  const handleReject = (id) => {
    setCars(prev => prev.map(car => car.id === id ? { ...car, status: 'rejected' } : car));
  };

  return (
    <div className="container">
      <h2>Pending Car Submissions</h2>
      {cars.length === 0 ? (
        <p>No pending cars.</p>
      ) : (
        <ul className="car-review-list">
          {cars.map(car => (
            <li key={car.id} className="car-review-item">
              <span><strong>{car.model}</strong> ({car.year}) - Status: <em>{car.status}</em></span>
              {car.status === 'pending' && (
                <div className="review-buttons">
                  <button onClick={() => handleApprove(car.id)} className="approve-button">Approve</button>
                  <button onClick={() => handleReject(car.id)} className="reject-button">Reject</button>
                </div>
              )}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default CarReview;
