// src/CarReview.js
import React, { useState, useEffect } from 'react';
import './style.css';

const CarReview = () => {
  const [cars, setCars] = useState([]);

  // Load all submitted cars from localStorage on component mount
  useEffect(() => {
    const storedCars = JSON.parse(localStorage.getItem('carSubmissions')) || [];
    setCars(storedCars);
  }, []);

  // Approve a car listing by setting its approved flag to true
  const handleApprove = (index) => {
    const updatedCars = [...cars];
    updatedCars[index].approved = true;
    localStorage.setItem('carSubmissions', JSON.stringify(updatedCars));
    setCars(updatedCars);
  };

  // Reject a car listing by removing it from the array
  const handleReject = (index) => {
    const updatedCars = [...cars];
    updatedCars.splice(index, 1);
    localStorage.setItem('carSubmissions', JSON.stringify(updatedCars));
    setCars(updatedCars);
  };

  return (
    <div className="container">
      <h1 className="dashboard-title">Pending Car Submissions</h1>
      <p className="dashboard-welcome">Review unapproved submissions below:</p>

      {/* If there are no unapproved cars, show message */}
      {cars.filter(car => !car.approved).length === 0 ? (
        <p>No pending submissions.</p>
      ) : (
        <ul className="dashboard-links">
          {cars.map((car, index) =>
            !car.approved ? (
              <li key={index}>
                <strong>{car.make} {car.model} ({car.year})</strong><br />
                Price: ${car.price}<br />
                Submitted by: {car.submittedBy || 'Unknown'}
                <div style={{ marginTop: '8px' }}>
                  <button
                    className="primary-button"
                    style={{ marginRight: '10px' }}
                    onClick={() => handleApprove(index)}
                  >
                    Approve
                  </button>
                  <button
                    className="todo-delete-button"
                    onClick={() => handleReject(index)}
                  >
                    Reject
                  </button>
                </div>
              </li>
            ) : null
          )}
        </ul>
      )}
    </div>
  );
};

export default CarReview;
