// src/SubmitCar.js
import React, { useState } from 'react';

const SubmitCar = () => {
  const [carData, setCarData] = useState({
    make: '',
    model: '',
    year: '',
    price: '',
  });

  // Handle input changes
  const handleChange = (e) => {
    setCarData({
      ...carData,
      [e.target.name]: e.target.value,
    });
  };

  // Submit form data
  const handleSubmit = (e) => {
    e.preventDefault();

    // Get current user info from localStorage
    const currentUser = JSON.parse(localStorage.getItem('currentUser'));
    if (!currentUser) {
      alert('Please log in first.');
      return;
    }

    // Combine car info with user name and 'approved' flag
    const newSubmission = {
      ...carData,
      submittedBy: currentUser.name,
      approved: false, // default to false
    };

    // Save to localStorage
    const storedCars = JSON.parse(localStorage.getItem('submittedCars')) || [];
    storedCars.push(newSubmission);
    localStorage.setItem('submittedCars', JSON.stringify(storedCars));

    alert('Car submitted for review!');
    // Don't reset form to preserve values
  };

  return (
    <div className="container">
      <h2>Submit a Used Car</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="make">Car Make</label>
          <input
            className="form-input"
            type="text"
            id="make"
            name="make"
            value={carData.make}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="model">Model</label>
          <input
            className="form-input"
            type="text"
            id="model"
            name="model"
            value={carData.model}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="year">Year</label>
          <input
            className="form-input"
            type="text"
            id="year"
            name="year"
            value={carData.year}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="price">Price</label>
          <input
            className="form-input"
            type="text"
            id="price"
            name="price"
            value={carData.price}
            onChange={handleChange}
            required
          />
        </div>

        <button className="primary-button" type="submit">
          Submit
        </button>
      </form>
    </div>
  );
};

export default SubmitCar;
