// src/ManageUsers.js
import React, { useState, useEffect } from 'react';
import './style.css';

const ManageUsers = () => {
  const [users, setUsers] = useState([]);
  const [newName, setNewName] = useState('');

  // Only run this once on mount
  useEffect(() => {
    let storedUsers = JSON.parse(localStorage.getItem('registeredUsers')) || [];

    // Check if admin exists
    const hasAdmin = storedUsers.some(user => user.role === 'Admin');

    if (!hasAdmin) {
      const adminUser = {
        id: crypto.randomUUID(),
        name: 'admin',
        role: 'Admin',
      };
      storedUsers = [adminUser, ...storedUsers];
      localStorage.setItem('registeredUsers', JSON.stringify(storedUsers));
    }

    setUsers(storedUsers);
  }, []);

  const handleAddUser = () => {
    if (newName.trim() === '') return;

    const newUser = {
      id: crypto.randomUUID(), // unique ID!
      name: newName.trim(),
      role: 'User',
    };

    const updatedUsers = [...users, newUser];
    setUsers(updatedUsers);
    localStorage.setItem('registeredUsers', JSON.stringify(updatedUsers));
    setNewName('');
  };

  const handleDeleteUser = (id) => {
    const userToDelete = users.find(user => user.id === id);
    if (userToDelete.role === 'Admin') {
      alert("Admin cannot be deleted.");
      return;
    }

    const updatedUsers = users.filter(user => user.id !== id);
    setUsers(updatedUsers);
    localStorage.setItem('registeredUsers', JSON.stringify(updatedUsers));
  };

  return (
    <div className="container">
      <h1 className="dashboard-title">Manage Users</h1>
      <p className="dashboard-welcome">List of registered users:</p>

      {/* Add user form */}
      <div className="user-form">
        <input
          type="text"
          placeholder="Enter name"
          value={newName}
          onChange={(e) => setNewName(e.target.value)}
          className="form-input"
        />
        <button onClick={handleAddUser} className="primary-button add-button">
          Add
        </button>
      </div>

      {/* User list */}
      {users.length === 0 ? (
        <p style={{ color: '#888', textAlign: 'center' }}>
          No users found. Please add one above.
        </p>
      ) : (
        <ul className="user-list">
          {users.map(user => (
            <li key={user.id} className="user-row">
              <span className="user-name">{user.name}</span>
              <span className="user-role-label">{user.role}</span>
              <button
                onClick={() => handleDeleteUser(user.id)}
                className="delete-button"
                disabled={user.role === 'Admin'} // can't delete admin
              >
                Delete
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default ManageUsers;
