// src/CarReview.js
import React, { useEffect, useState } from 'react';
import './style.css';

const CarReview = () => {
  const [pendingCars, setPendingCars] = useState([]);

  // Load pending cars from localStorage
  useEffect(() => {
    const allCars = JSON.parse(localStorage.getItem('carSubmissions')) || [];
    const pending = allCars.filter(car => car.status === 'pending');
    setPendingCars(pending);
  }, []);

  // Handle approve action
  const handleApprove = (carId) => {
    // Get all cars
    const allCars = JSON.parse(localStorage.getItem('carSubmissions')) || [];

    // Update status to 'approved'
    const updatedCars = allCars.map(car =>
      car.id === carId ? { ...car, status: 'approved' } : car
    );

    // Save back to localStorage
    localStorage.setItem('carSubmissions', JSON.stringify(updatedCars));

    // Update local state
    const updatedPending = updatedCars.filter(car => car.status === 'pending');
    setPendingCars(updatedPending);
  };

  return (
    <div className="container">
      <h2 className="dashboard-title">Pending Car Submissions</h2>
      {pendingCars.length === 0 ? (
        <p>No pending submissions.</p>
      ) : (
        <ul className="dashboard-links">
          {pendingCars.map(car => (
            <li key={car.id}>
              <strong>{car.make} {car.model}</strong> – {car.year} – ${car.price}
              <button
                onClick={() => handleApprove(car.id)}
                className="primary-button"
                style={{ marginTop: '10px' }}
              >
                Approve
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default CarReview;
