// src/ManageUsers.js
import React, { useState, useEffect } from 'react';
import './style.css';

const ManageUsers = () => {
  const [users, setUsers] = useState([]);
  const [newName, setNewName] = useState('');

  // Load users from localStorage on component mount
  useEffect(() => {
    const storedUsers = JSON.parse(localStorage.getItem('registeredUsers')) || [];

    // Ensure admin exists
    const adminExists = storedUsers.some(user => user.role === 'Admin');
    let updatedUsers = [...storedUsers];

    if (!adminExists) {
      const adminUser = {
        id: 'admin-' + Date.now(), // static admin ID to prevent collision
        name: 'admin',
        role: 'Admin',
      };
      updatedUsers.unshift(adminUser);
    }

    setUsers(updatedUsers);
    localStorage.setItem('registeredUsers', JSON.stringify(updatedUsers));
  }, []);

  // Handle adding a new user
  const handleAddUser = () => {
    if (newName.trim() === '') return;

    const newUser = {
      id: Date.now().toString() + '-' + Math.random().toString(36).substring(2, 8), // Ensure unique ID
      name: newName.trim(),
      role: 'User',
    };

    const updatedUsers = [...users, newUser];
    setUsers(updatedUsers);
    localStorage.setItem('registeredUsers', JSON.stringify(updatedUsers));
    setNewName('');
  };

  // Handle deleting a user by ID, except admin
  const handleDeleteUser = (id) => {
    const userToDelete = users.find(user => user.id === id);
    if (userToDelete && userToDelete.role === 'Admin') {
      alert('Cannot delete admin user.');
      return;
    }

    const updatedUsers = users.filter(user => user.id !== id);
    setUsers(updatedUsers);
    localStorage.setItem('registeredUsers', JSON.stringify(updatedUsers));
  };

  return (
    <div className="container">
      <h1 className="dashboard-title">Manage Users</h1>
      <p className="dashboard-welcome">List of registered users:</p>

      {/* Input form to add user */}
      <div className="user-form">
        <input
          type="text"
          placeholder="Enter name"
          value={newName}
          onChange={(e) => setNewName(e.target.value)}
          className="form-input"
        />
        <button onClick={handleAddUser} className="primary-button add-button">
          Add
        </button>
      </div>

      {/* Show message if no users */}
      {users.length === 0 ? (
        <p style={{ color: '#888', textAlign: 'center' }}>
          No users found. Please add one above.
        </p>
      ) : (
        <ul className="user-list">
          {users.map(user => (
            <li key={user.id} className="user-row">
              <span className="user-name">{user.name}</span>
              <span className="user-role-label">{user.role}</span>

              {/* Only show delete button for non-admins */}
              {user.role !== 'Admin' && (
                <button
                  onClick={() => handleDeleteUser(user.id)}
                  className="delete-button"
                >
                  Delete
                </button>
              )}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default ManageUsers;
