import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // React Router hook for navigation
import './style.css';

const Login = () => {
  // State for username and role
  const [username, setUsername] = useState('');
  const [role, setRole] = useState('User');
  const navigate = useNavigate(); // Hook for programmatic route changes

  // Handle login button click
  const handleLogin = () => {
    alert(`Logging in as ${username} with role ${role}`);

    // Redirect user based on selected role
    if (role === 'Admin') {
      navigate('/admin'); // Navigate to Admin Dashboard
    } else {
      navigate('/user'); // Navigate to User Dashboard
    }
  };

  return (
    <div className="container">
      <h2>Login</h2>

      {/* Username input field */}
      <div className="form-group">
        <label>Username</label>
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          className="form-input"
        />
      </div>

      {/* Role selection dropdown */}
      <div className="form-group">
        <label>Role</label>
        <select
          value={role}
          onChange={(e) => setRole(e.target.value)}
          className="form-select"
        >
          <option>User</option>
          <option>Admin</option>
        </select>
      </div>

      {/* Login button */}
      <button onClick={handleLogin} className="primary-button">
        Login
      </button>
    </div>
  );
};

export default Login;
