// Programming Lab 3: Todo List Application in React
// This component fulfills Lab 3 by building a todo list app,
// fetching data from a public API using Fetch API, and handling state & events.

import React, { useState, useEffect } from 'react';

const TodoList = () => {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');

  // Fetch data from a public API (JSONPlaceholder) using Fetch API
  useEffect(() => {
    fetch('https://jsonplaceholder.typicode.com/todos?_limit=5')
      .then((res) => res.json())
      .then((data) => {
        const titles = data.map((item) => item.title);
        setTasks(titles);
      });
  }, []);

  // Add a new task
  const addTask = () => {
    if (newTask.trim() !== '') {
      setTasks([...tasks, newTask]);
      setNewTask('');
    }
  };

  // Delete a task by index
  const deleteTask = (index) => {
    const updatedTasks = [...tasks];
    updatedTasks.splice(index, 1);
    setTasks(updatedTasks);
  };

  return (
    <div className="container">
      <h2>Todo List</h2>
      <div className="todo-input-group">
        <input
          type="text"
          placeholder="Enter a task"
          className="todo-input"
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
        />
        <button className="todo-add-button" onClick={addTask}>
          Add
        </button>
      </div>

      <ul className="todo-list">
        {tasks.map((task, index) => (
          <li key={index} className="todo-item">
            {task}
            <button
              className="todo-delete-button"
              onClick={() => deleteTask(index)}
            >
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TodoList;
