// src/CarReview.js
import React, { useEffect, useState } from 'react';
import './style.css';

const CarReview = () => {
  const [pendingCars, setPendingCars] = useState([]);

  useEffect(() => {
    // 模拟加载数据
    setTimeout(() => {
      const mockData = [
        { id: 1, make: 'Toyota', model: 'Corolla', year: 2020, status: 'pending' },
        { id: 2, make: 'Honda', model: 'Civic', year: 2019, status: 'pending' },
      ];
      setPendingCars(mockData);
    }, 500);
  }, []);

  const handleApprove = (id) => {
    setPendingCars(prevCars =>
      prevCars.map(car =>
        car.id === id ? { ...car, status: 'approved' } : car
      )
    );
  };

  return (
    <div className="container">
      <h1 className="dashboard-title">Pending Car Submissions</h1>
      <ul>
        {pendingCars.map(car => (
          <li key={car.id} style={{ marginBottom: '10px' }}>
            <strong>{car.make} {car.model}</strong> ({car.year}) - Status: <em>{car.status}</em>
            {car.status === 'pending' && (
              <button
                className="primary-button"
                style={{ width: 'auto', marginLeft: '10px', padding: '5px 10px' }}
                onClick={() => handleApprove(car.id)}
              >
                Approve
              </button>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default CarReview;
