// src/ManageUsers.js
import React, { useState } from 'react';
import './style.css';

const ManageUsers = () => {
  // Initialize default list of users
  const [users, setUsers] = useState([
    { id: 1, name: 'Alice', role: 'User' },
    { id: 2, name: 'Bob', role: 'Admin' },
    { id: 3, name: 'Charlie', role: 'User' },
  ]);

  // State for new user input
  const [newName, setNewName] = useState('');
  const [newRole, setNewRole] = useState('User');

  // Handle adding a new user to the list
  const handleAddUser = () => {
    if (newName.trim() === '') return; // Prevent adding empty names
    const newUser = {
      id: Date.now(), // Unique ID based on timestamp
      name: newName,
      role: newRole,
    };
    setUsers([...users, newUser]); // Add to state
    setNewName('');      // Clear name field
    setNewRole('User');  // Reset role selector
  };

  // Handle deleting a user by ID
  const handleDelete = (id) => {
    setUsers(users.filter(user => user.id !== id));
  };

  // Handle updating the role of a user
  const handleRoleChange = (id, newRole) => {
    setUsers(users.map(user =>
      user.id === id ? { ...user, role: newRole } : user
    ));
  };

  return (
    <div className="container">
      <h1 className="dashboard-title">Manage Users</h1>
      <p className="dashboard-welcome">List of registered users:</p>

      {/* Input form to add a new user */}
      <div style={{ display: 'flex', gap: '10px', marginBottom: '20px' }}>
        <input
          type="text"
          placeholder="Enter name"
          value={newName}
          onChange={(e) => setNewName(e.target.value)}
          className="form-input"
          style={{ flex: 1 }}
        />
        <select
          value={newRole}
          onChange={(e) => setNewRole(e.target.value)}
          className="form-select"
        >
          <option value="User">User</option>
          <option value="Admin">Admin</option>
        </select>
        <button
          onClick={handleAddUser}
          className="primary-button"
          style={{ padding: '8px 12px' }}
        >
          Add
        </button>
      </div>

      {/* Display list of users with editable roles and delete buttons */}
      <ul className="dashboard-links">
        {users.map(user => (
          <li key={user.id} style={{ marginBottom: '12px' }}>
            <strong>{user.name}</strong> â€“ Role:&nbsp;
            <select
              value={user.role}
              onChange={(e) => handleRoleChange(user.id, e.target.value)}
              style={{ marginRight: '10px' }}
            >
              <option value="User">User</option>
              <option value="Admin">Admin</option>
            </select>
            <button
              onClick={() => handleDelete(user.id)}
              style={{
                padding: '4px 10px',
                backgroundColor: '#dc3545',
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer'
              }}
            >
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ManageUsers;
