// src/ManageUsers.js
import React, { useState, useEffect } from 'react';
import './style.css';

const ManageUsers = () => {
  const [users, setUsers] = useState([]);
  const [newName, setNewName] = useState('');

  // Load users from localStorage on component mount
  useEffect(() => {
    try {
      const stored = JSON.parse(localStorage.getItem('registeredUsers')) || [];
      const cleaned = stored.filter(u => u.id && u.name); // Filter out corrupted data
      setUsers(cleaned);
    } catch (err) {
      console.error("Error reading users from localStorage", err);
      setUsers([]);
    }
  }, []);

  // Add a new user with role "User"
  const handleAddUser = () => {
    const trimmed = newName.trim();
    if (trimmed === '') {
      alert("Please enter a valid name.");
      return;
    }

    const newUser = {
      id: Date.now(), // Use timestamp as unique ID
      name: trimmed,
      role: 'User' // Always assign role 'User'
    };

    const updated = [...users, newUser];
    setUsers(updated);
    localStorage.setItem('registeredUsers', JSON.stringify(updated));
    setNewName('');
  };

  // Delete user with confirmation; prevent deleting Admin
  const handleDeleteUser = (id) => {
    const userToDelete = users.find(user => user.id === id);
    if (!userToDelete) {
      alert("User not found.");
      return;
    }

    if (userToDelete.role === 'Admin') {
      alert("Admin user cannot be deleted.");
      return;
    }

    const confirmDelete = window.confirm(`Are you sure you want to delete "${userToDelete.name}"?`);
    if (!confirmDelete) return;

    const updated = users.filter(user => user.id !== id);
    setUsers(updated);
    localStorage.setItem('registeredUsers', JSON.stringify(updated));
  };

  // Find current Admin (there should be only one)
  const currentAdmin = users.find(user => user.role === 'Admin');

  return (
    <div className="container">
      <h1 className="dashboard-title">Manage Users</h1>
      <p className="dashboard-welcome">List of registered users:</p>

      {/* Input field to add new users */}
      <div className="user-form">
        <input
          type="text"
          placeholder="Enter name"
          value={newName}
          onChange={(e) => setNewName(e.target.value)}
          className="form-input"
        />
        <button onClick={handleAddUser} className="primary-button add-button">
          Add
        </button>
      </div>

      {/* Show current Admin user */}
      {currentAdmin && (
        <p style={{ marginBottom: '1rem', fontWeight: 'bold' }}>
          Current Admin: {currentAdmin.name}
        </p>
      )}

      {/* User list display */}
      <ul className="user-list">
        {users.map(user => (
          <li key={user.id} className="user-row">
            <span className="user-name">{user.name}</span>
            <span className="user-role-label">{user.role}</span>
            {/* Allow deletion only for non-Admin users */}
            {user.role !== 'Admin' && (
              <button
                onClick={() => handleDeleteUser(user.id)}
                className="delete-button"
              >
                Delete
              </button>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ManageUsers;
