// Programming Lab 2 – Home Page Component (With Image from Public Folder)
import React from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
  return (
    <div className="container mx-auto px-4 py-8">
      <div className="bg-white p-6 rounded-lg shadow-md text-center">

        {/* Welcome title */}
        <h1 className="text-3xl font-bold mb-4">
          Welcome to the Used Car Marketplace!
        </h1>

        {/* Car image from public/Image folder */}
        <img
          src="/Image/ferrari-812-competizione.jpg" // ✅ 注意前面有 /
          alt="Featured used car"
          className="mx-auto mb-4 rounded-lg shadow"
          style={{ maxHeight: '300px', objectFit: 'cover' }}
        />

        {/* Intro text */}
        <p className="text-gray-700 mb-4">
          Browse and list used cars with ease.
        </p>

        {/* Feature links */}
        <ul className="text-left text-gray-700 list-disc list-inside mb-4">
          <li>
            ✔ <Link to="/listings" className="text-blue-500 hover:underline">
              Browse approved listings
            </Link>
          </li>
          <li>
            ✔ <Link to="/login" className="text-blue-500 hover:underline">
              Admin login for car review
            </Link>
          </li>
        </ul>
      </div>
    </div>
  );
};

export default Home;
